import makeStyles from "@material-ui/core/styles/makeStyles"

const useStyles = makeStyles((theme) =>({  
 
    product : {
        padding : "5px",
        margin : "1px 20px",
        height:  "300px",
        minWidth :"360px",
        maxWidth : "300px",
        maxHeight : "400px"
    },
    image : {
        width :"100px",
        backgroundSize : "contain",
        maxWidth: "300px"
    }
}));

export default useStyles;
