import React, { useState ,useEffect } from 'react'
import useStyles from "./styles"


/*  Material UI  */

import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import { Container,Dialog,DialogTitle,DialogContent,DialogActions,TextField, Grid, Button, InputLabel, FormControl,Select,Input } from '@material-ui/core';

/*  APIs  */

import { createCategory } from '../../api/category';

import { createProduct } from '../../api/product';

/*  Validators  */

import isEmpty from "validator/lib/isEmpty"
import { showErrorMsg } from "../../helper/showErrorMsg"
import { showSuccessMsg } from "../../helper/showSuccessMsg"
import { Loading } from "../../helper/Loading"

/*  Components  */

import AdminHeader from "./AdminHeader"

/* REACT REDUX */

import {useSelector,useDispatch} from "react-redux"
import { clearMessages } from  "../../redux/actions/messageActions"
import { getCategories } from '../../../src/redux/actions/categoryActions';
import { createCategories } from '../../../src/redux/actions/categoryActions';


/*  Main Function  */

const AdminDashboard = () => {

  const classes = useStyles();




  /**
   REDUX GLOBAL STATE Properties 
   */

   const  {successMsg,errorMsg} = useSelector(state => state.messages)
   const  { loading } = useSelector(state => state.loading)





  //  States

  const [categories,setCategories] = useState(null);
  const [openCategory, setOpenCategory] = useState(false);
  const [openProduct, setOpenProduct] = useState(false);
  const [category, setCategory] = useState("");


const handleMessages = evt=> {
  dispatchEvent(clearMessages())
}
const handleCategoryChange= evt=> {
  dispatchEvent(clearMessages())
  setCategory(evt.target.value)
}


  const [productData,setProductData] = useState({
    productImage : null ,
    productName : "",
    productDescription :"",
    productPrice : "",
    productCategory : "",
    productQty : ""
  })

 //Destructered productData
  const 
  {
  productImage,
  productName,
  productDescription,
  productPrice,
  productCategory,
  productQty
 }  = productData;


/*********************
 * LifeCycle Events
 * ******************/

useEffect(() => {
  getCategories
}, [loading])


/* API CALL Aciton   */

// const loadCategories =  async()  => {
//     await getCategories()
//         .then((response) => {
//             setCategories(response.data.categories);
//             console.log(response.data.categories)

//         })
//         .catch((err) =>{
//             console.log(err.message);
//         })
// }


/*********************
 * Event Handlers
 * ******************/

  const handleClickOpenCategory = () => {
    setSuccessMsg("")
    setErrorMsg("");
    setOpenCategory(true);
  };
  const handleCloseCategory = () => {
    setOpenCategory(false);
  };
  const handleClickOpenProduct = () => {
    setSuccessMsg("")
    setErrorMsg("");
    setOpenProduct(true);
  };
  const handleCloseProduct = () => {
    setOpenProduct(false);
  };
  const handlechangeCategory = (e) => {
    setErrorMsg("");
    setSuccessMsg("");
    setCategory(e.target.value);
  }
  const handleSubmitCategory = (e) => {
    e.preventDefault();
    setCategory("");
    if (isEmpty(category)) {
      setErrorMsg("Please Enter a Category");
    }
    else {
      const data = { category };
      setLoading(true);
      createCategory(data)
        .then(response => {
          setLoading(false);
          setSuccessMsg(response.data.successMessage);
        })
        .catch(err => {
          setLoading(false);
          setErrorMsg(err.response.data.errorMessage);
        })
    }

  }
  const handleSubmitProduct = (e) => {
    console.log(productName,productQty,productDescription)
    e.preventDefault();
    if (!productImage) {
      setErrorMsg("Please select an Image ");
    } 
    else if(isEmpty(productName) || isEmpty(productDescription)){
      setErrorMsg("All Fields are requierd ");
    } else if(isEmpty(productCategory)){
      setErrorMsg("Please select a Category ");
    } else if(isEmpty(productQty)){
      setErrorMsg("Please select  the Quantity of the product ");
    } else {
      setErrorMsg("");      

      // let formData = productData;
      // console.log(productImage)
      let formData = new FormData();

      formData.append('productImage',productImage);
      formData.append('productName',productName);
      formData.append('productDescription',productDescription);
      formData.append('productCategory',productCategory);
      formData.append('productPrice',productPrice);
      formData.append('productQty',productQty);
      
      createProduct(formData)
        .then((response) => {

          setProductData({
            productImage : null ,
            productName : "",
            productDescription :"",
            productPrice : "",
            productCategory : "",
            productQty : ""
          })

          setSuccessMsg(response.data.successMessage);

        })
        .catch((err) => {
          setErrorMsg(err.response.data.errorMessage)
          console.log(err);
          console.log("Error while post req front")
        })


    }
  }
  const handleProductChange = (evt) => {
    console.log( evt.target.value)
   setProductData({
     ...productData,
     [evt.target.name] : evt.target.value
   })
  }
  const handleProductImage = (evt) => {
    setProductData({
      ...productData,
      [evt.target.name] : evt.target.files[0]
    })
  }

/*
      RENDER FUNCTION
*/
  return (
    <section>
      <CssBaseline />
      <AdminHeader />
      <div className={classes.parent}>

        {/* Action Buttons  */}

        <Grid container>
          <Grid item xs={4}>
            <Button className={classes.catbutton} variant="outlined" color="primary" onClick={handleClickOpenCategory}>
              <Typography>
                Add Category
                  </Typography>
            </Button>
          </Grid>
          <Grid item xs={4}>
            <Button className={classes.catbutton} variant="outlined" color="primary" onClick={handleClickOpenProduct}>
              <Typography>
                Add Food
                  </Typography>
            </Button>
          </Grid>
          <Grid item xs={4}>
            <Button className={classes.catbutton} variant="outlined" color="primary" onClick={handleClickOpenProduct}>
              <Typography>
                View Orders
                  </Typography>
            </Button>
          </Grid>
        </Grid>
    
         {/* Add Category Dialog */}

        <Dialog open={openCategory} onClose={handleCloseCategory} aria-labelledby="form-dialog-title" fullWidth>
          <DialogTitle id="form-dialog-title" className={classes.TitleItem}>Add category</DialogTitle>

          <form onSubmit={handleSubmitCategory} >
            {errorMsg && showErrorMsg(errorMsg)}
            {successMsg && showSuccessMsg(successMsg)}
            {
              loading ? (
                Loading()
              ) : (
                <DialogContent >
                  <Typography variant="h6" >Category :  {category}</Typography>
                  <TextField
                    autoFocus
                    margin="dense"
                    id="name"
                    label="Category Name"
                    type="name"
                    value={category}
                    fullWidth
                    variant="outlined"
                    onChange={(e) => handlechangeCategory(e)}
                  />

                </DialogContent>
              )
            }
            <DialogActions>
              <Button onClick={handleCloseCategory} color="primary">
                Cancel
              </Button>
              <Button type="submit" color="primary">
                Submit
              </Button>

            </DialogActions>
          </form>
        </Dialog>

          {/* Add Food Dialogue */}

        <Dialog open={openProduct} onClose={handleCloseProduct} aria-labelledby="form-dialog-title">
          <DialogTitle id="form-dialog-title" className={classes.TitleItem}>Add Product</DialogTitle>

          {/* Form */}

          <form onSubmit={handleSubmitProduct}>
            {errorMsg && showErrorMsg(errorMsg)}
            {successMsg && showSuccessMsg(successMsg)}
            {
              loading ? (
                Loading()
              ) : (
                <DialogContent >
                  <Typography variant="h6">Product Details :</Typography>
                  <br />
                  <span className={classes.span}>Choose Product </span> 
                  <input className={classes.inputFile} onChange={handleProductImage}  name="productImage" type="file" />
                  <br />
                  <br />
                  <TextField
                    autoFocus
                    type="name"
                    margin="dense"
                    name="productName"
                    value={productName}
                    placeholder="Type Name"
                    variant="outlined"
                    onChange={handleProductChange}
                    fullWidth
                  />
                  <TextField
                    margin="dense"
                    placeholder=" Write Description"
                    name="productDescription"
                    value={productDescription}
                    multiline="true"
                    rows="4"
                    variant="outlined"
                    onChange={handleProductChange}
                    fullWidth
                  />
                  <TextField
                    autoFocus
                    type="price"
                    margin="dense"
                    placeholder="Write Price"
                    name="productPrice"
                    value={productPrice}
                    variant="outlined"
                    onChange={handleProductChange}
                    fullWidth
                  />

                  <FormControl variant="outlined" className={classes.formControl} halfWidth>
                    <InputLabel htmlFor="outlined-age-native-simple">Select Category</InputLabel>
                    <Select 
                      native
                      placeholder = "Selct Category"
                      name="productCategory"
                      value={productCategory}
                      onChange={handleProductChange}
                     
                    >
                      <option value="">Choose a Category</option>


                      {categories && 
                          categories.map((category) => (
                           <option
                            key={category._id}
                            value={category._id}
                            >
                            {category.category}
                            </option>
                          ))}
                    </Select>
                  </FormControl>


                <TextField
                autoFocus
                type="number"  
                variant="outlined"
                margin="dense"
                placeholder="Quantity"
                name="productQty"
                onChange={handleProductChange}
                value={productQty}
                fullWidth
                />
                <hr style={{borderWidth : "0px"}}/>
                <hr style={{borderWidth : "0px"}}/>

                </DialogContent>
              )
            }
            <DialogActions>
              <Button onClick={handleCloseProduct} color="primary">
                Cancel
              </Button>
              <Button type="submit" color="primary">
                Submit
              </Button>

            </DialogActions>
          </form>
        </Dialog>

      </div>
    </section>
  )
}

export default AdminDashboard
