import React,{useState} from 'react'
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import {Container,Grid,Button} from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import useStyles from "./styles"
import { createCategory } from '../../api/category';
import  isEmpty from "validator/lib/isEmpty"
import {showErrorMsg} from "../../helper/showErrorMsg"
import {showSuccessMsg} from "../../helper/showSuccessMsg"
import {Loading} from "../../helper/Loading"


const AdminDashboard = () => {
  const classes = useStyles();
  const [openCategory, setOpenCategory] =  useState(false);
  const [openProduct, setOpenProduct] =  useState(false);
  const [category,setCategory] = useState("");
  const [errorMsg,setErrorMsg] = useState("");
  const [successMsg,setSuccessMsg] = useState("");
  const [loading,setLoading] = useState(false);

  const handleClickOpenCategory = () => {
    setOpen(true);
  };

  const handleCloseCategory = () => {
    setOpen(false);
  };

  const handleClickOpenProduct = () => {
    setOpen(true);
  };

  const handleCloseProduct = () => {
    setOpen(false);
  };




  const handlechange = (e) => {
    setErrorMsg("");
    setSuccessMsg("");
    setCategory(e.target.value);
  }
  const handleSubmitCategory = (e) =>{
    e.preventDefault();
    setCategory("");
    if(isEmpty(category)){
      setErrorMsg("Please Enter a Category");
    }
    else{
      const data = {category};
      setLoading(true);
      createCategory(data)
        .then(response => {
          setLoading(false);
          setSuccessMsg(response.data.successMessage);
        })
        .catch( err => {
          setLoading(false);
          setErrorMsg(err.response.data.errorMessage);
        })
    }

  }

    return (
    <section>
      <CssBaseline />
      <div className={classes.parent}>  
            <Typography className={classes.header} >DashBoard</Typography>    
            <Grid container>
              <Grid item xs= {4}>
                <Button className={classes.catbutton} variant="outlined" color="primary" onClick={handleClickOpenCategory}>
                  <Typography>
                    Add Category
                  </Typography>
                </Button>
              </Grid>
              <Grid item xs= {4}>
                <Button className={classes.catbutton} variant="outlined" color="primary" onClick={handleClickOpenProduct}>
                  <Typography>
                    Add Product 
                  </Typography>
                </Button>
              </Grid>
              <Grid item xs= {4}>
                <Button className={classes.catbutton} variant="outlined" color="primary" onClick={handleClickOpen}>
                  <Typography>
                    View Orders
                  </Typography>
                </Button>
              </Grid>
          </Grid>

          <Dialog open={openCategory} onClose={handleCloseCategory} aria-labelledby="form-dialog-title">
            <DialogTitle id="form-dialog-title">Add category</DialogTitle>

            <form onSubmit={handleSubmitCategory}>
            {errorMsg && showErrorMsg(errorMsg)}
            {successMsg && showSuccessMsg(successMsg)}
            {
              loading ? (
                Loading()
              ) :   (
                <DialogContent >
                <Typography >Category :  {category}</Typography>
                <br/>
              <TextField
                autoFocus
                margin="dense"  
                id="name"
                label="Category Name"
                type="name"
                value={category}
                fullWidth
                variant="outlined"
                onChange={(e)=>handlechangeCategory(e)}
              />

            </DialogContent>
              )
            }
            <DialogActions>
              <Button onClick={handleCloseCategory} color="primary">
                Cancel
              </Button> 
              <Button  type="submit"  color="primary">
                Submit
              </Button>

            </DialogActions>
            </form>
          </Dialog>
    

          <Dialog open={openProduct} onClose={handleCloseProduct} aria-labelledby="form-dialog-title">
            <DialogTitle id="form-dialog-title">Add category</DialogTitle>

            <form onSubmit={handleSubmitProduct}>
            {errorMsg && showErrorMsg(errorMsg)}
            {successMsg && showSuccessMsg(successMsg)}
            {
              loading ? (
                Loading()
              ) :   (
                <DialogContent >
                <Typography >Category :  {category}</Typography>
                <br/>
              <TextField
                autoFocus
                margin="dense"  
                id="name"
                label="Category Name"
                type="name"
                value={category}
                fullWidth
                variant="outlined"
                onChange={(e)=>handlechangeProduct(e)}
              />

            </DialogContent>
              )
            }
            <DialogActions>
              <Button onClick={handleCloseProduct} color="primary">
                Cancel
              </Button> 
              <Button  type="submit"  color="primary">
                Submit
              </Button>

            </DialogActions>
            </form>
          </Dialog>
    
    
    
    
    
    
    
      </div>

    </section>
    )
}

export default AdminDashboard
