import React from 'react'

import useStyles from "./styles.js"
const Product = (prod) => {

    const classes = useStyles();
    prod = prod.product;  
  
    return (
        <>
            <div className={classes.product}>

                <p><span>Name : {prod.productName}</span> </p>
                <p><span>Price :  {prod.productPrice}</span></p>
                <p><span>  Quantity :{prod.productQty} </span></p>
                <p><span> Description : {prod.productDescription}</span> </p>
                <p><span>     Category :{prod.productCategory?.category}</span></p>
                <hr />
            </div>
        </>
    )
}

export default Product
