import React,{useState,useEffect} from 'react'
import { useParams } from 'react-router-dom'
import {useDispatch,useSelector} from "react-redux"
import Product from '../AllProducts/Product';
import {getProduct, getProducts} from "../../redux/actions/productActions"
import {getCategories} from "../../redux/actions/categoryActions"

const AdminEditProduct = (props) => {

        /*****
         * PARAMS 
         */
    // const productId = useParams();

    /*****
     * Component State Properties 
     */
    const productId = props.match.params.productId;
	const [productImage, setProductImage] = useState(null);
	const [productName, setProductName] = useState('');
	const [productDescription, setProductDescription] = useState('');
	const [productPrice, setProductPrice] = useState('');
	const [productCategory, setProductCategory] = useState('');
	const [productQty, setProductQty] = useState('');

    // console.log(productId)

    const dispatch = useDispatch();
    const {product} = useSelector(state => state.products);
    const {categories} = useSelector(state => state.categories);
 
    useEffect(() => {
      
        if(!product){
            dispatch(getProduct(productId))
            dispatch(getCategories())
            
        }else {
            setProductImage(product.filename);
            setProductName(product.productName);
            setProductDescription(product.productDescription);
            setProductPrice(product.productPrice);
            setProductCategory(product.setProductCategory);
            setProductQty(product.productQty);
        }
    }, [dispatch,productId,product])
     
    return (
        <div>
            {JSON.stringify(categories)}
            Inside Edit Product
            {product?.productName}
            {product?.productCategory}


            <form onSubmit={handleSubmitProduct}>
            {clientSideErrorMsg && showErrorMsg(clientSideErrorMsg)}
        
            {successMsg && showSuccessMsg(successMsg)}
            {
              loading ? (
                Loading()
              ) : (
                <DialogContent >
                  <Typography variant="h6">Product Details :</Typography>
                  <br />
                  <span className={classes.span}>Choose Product </span> 
                  <input className={classes.inputFile} onChange={handleProductImage}  name="productImage" type="file" />
                  <br />
                  <br />
                  <TextField
                    autoFocus
                    type="name"
                    margin="dense"
                    name="productName"
                    value={productName}
                    placeholder="Type Name"
                    variant="outlined"
                    onChange={handleProductChange}
                    fullWidth
                  />
                  <TextField
                    margin="dense"
                    placeholder=" Write Description"
                    name="productDescription"
                    value={productDescription}
                    multiline="true"
                    rows="4"
                    variant="outlined"
                    onChange={handleProductChange}
                    fullWidth
                  />
                  <TextField
                    autoFocus
                    type="price"
                    margin="dense"
                    placeholder="Write Price"
                    name="productPrice"
                    value={productPrice}
                    variant="outlined"
                    onChange={handleProductChange}
                    fullWidth
                  />

                  <FormControl variant="outlined" className={classes.formControl} halfWidth>
                    <InputLabel htmlFor="outlined-age-native-simple">Select Category</InputLabel>
                    <Select 
                      native
                      placeholder = "Selct Category"
                      name="productCategory"
                      value={productCategory}
                      onChange={handleProductChange}
                     
                    >
                      <option value="">Choose a Category</option>


                      {categories && 
                          categories.map((cat) => (
                            <option
                              key={cat._id}
                              value={cat._id}
                            >
                             {cat.category}
                            </option>
                      ))}
                    </Select>
                  </FormControl>


                <TextField
                autoFocus
                type="number"  
                variant="outlined"
                margin="dense"
                placeholder="Quantity"
                name="productQty"
                onChange={handleProductChange}
                value={productQty}
                fullWidth
                />
                <hr style={{borderWidth : "0px"}}/>
                <hr style={{borderWidth : "0px"}}/>

                </DialogContent>
              )
            }

              <Button color="primary">
                Cancel
              </Button>
              <Button type="submit" color="primary">
                Submit
              </Button>
          </form>











        </div>
    )
}

export default AdminEditProduct
